	public void readTopCities() throws IOException {

		BufferedReader br = new BufferedReader(new FileReader(
				"Z:/Workspace/dii_project/resources/top_cities.txt"));
		try {
			StringBuilder sb = new StringBuilder();
			String line = br.readLine();
			String city;
			String json;
			while (line != null) {

				int i = line.indexOf(",");
				city = line.substring(0, i);
				int j = line.indexOf("{");
				json = line.substring(j);
				char c = 92;
				int k = json.indexOf("[");
				String json_sub1 = json.substring(0, k);
				String json_sub2 = json.substring(k);
				json_sub2 = json_sub2.replaceAll("\"", "");
				// System.out.println("Sub2 : "+json_sub2);
				// System.out.println("Sub1 : "+json_sub1);
				json = json_sub1.concat(json_sub2);
				// json=json.substring(0, json.length()-1);
				json = json.replaceAll("\"\"", (new Character(c)).toString()
						+ "\"");
				// System.out.println("json: "+json);

				readJSON(json, city);

				line = br.readLine();
			}

		} finally {
			br.close();
		}
	}
	
